/**
 * Representa una colección de series que se pueden transmitir en streaming.
 */
import { BasicStreamableCollection } from "./BasicStreamableCollection";
import { Serie } from "./Interfaces";

export class SeriesCollection extends BasicStreamableCollection<Serie> {
    constructor(collection: Serie[]) {
        super(collection);
    }

    /**
     * Busca series por nombre.
     * @param name El nombre de la serie a buscar.
     * @returns Un arreglo de objetos Serie que coinciden con el nombre especificado.
     */
    searchName(name: string): Serie[] {
        const result = [];
        for (const serie of this.collection) {
            if (serie.nombre === name) {
                result.push(serie);
            }
        }
        return result;
    }

    /**
     * Busca series por año.
     * @param year El año de la serie a buscar.
     * @returns Un arreglo de objetos Serie que coinciden con el año especificado.
     */
    searchYear(year: number): Serie[] {
        const result = [];
        for (const serie of this.collection) {
            if (serie.año === year) {
                result.push(serie);
            }
        }
        return result;
    }

    /**
     * Busca series por número de temporadas.
     * @param seasons El número de temporadas de la serie a buscar.
     * @returns Un arreglo de objetos Serie que coinciden con el número de temporadas especificado.
     */
    searchSeasons(seasons: number): Serie[] {
        const result = [];
        for (const serie of this.collection) {
            if (serie.temporadas === seasons) {
                result.push(serie);
            }
        }
        return result;
    }
}

/**
 * La clase BasicStreamableCollection es una clase abstracta que implementa la interfaz Streamable y tiene
 * implementados dos métodos: searchName y searchYear.
 * @template T El tipo de elementos que hay en la colección.
 */
import { Streamable } from "./Streamable";

export abstract class BasicStreamableCollection<T> implements Streamable {
  /**
   * Crea una nueva instancia de la clase BasicStreamableCollection.
   * @param collection El array de elementos que se almacenará en la colección.
   */
  constructor(protected collection: T[]) {}

  /**
   * Busca elementos en la colección que coincidan con el nombre especificado.
   * @param name El nombre a buscar.
   * @returns Un array de elementos que coinciden con el nombre especificado.
   */
  abstract searchName(name: string): T[];

  /**
   * Busca elementos en la colección que fueron lanzados en el año especificado.
   * @param year El año a buscar.
   * @returns Un array de elementos que fueron lanzados en el año especificado.
   */
  abstract searchYear(year: number): T[];
}

/**
 * Clase que representa una colección de documentales, extiende BasicStreamableCollection.
 * @typeparam Documentarie Tipo de elemento que almacena la colección.
 */
import { BasicStreamableCollection } from "./BasicStreamableCollection";
import { Documentarie } from "./Interfaces";

export class DocumentalesCollection extends BasicStreamableCollection<Documentarie> {
    /**
     * Método que busca documentales por nombre.
     * @param name Nombre del documental a buscar.
     * @returns Array de documentales cuyo nombre coincide con el parámetro de entrada.
     */
    searchName(name: string): Documentarie[] {
        return this.collection.filter((documentales) => documentales.nombre === name);
    }

    /**
     * Método que busca documentales por año de lanzamiento.
     * @param year Año de lanzamiento del documental a buscar.
     * @returns Array de documentales cuyo año de lanzamiento coincide con el parámetro de entrada.
     */
    searchYear(year: number): Documentarie[] {
        return this.collection.filter((documentales) => documentales.año === year);
    }

    /**
     * Método que busca documentales por duración.
     * @param duration Duración del documental a buscar.
     * @returns Array de documentales cuya duración coincide con el parámetro de entrada.
     */
    searchDuration(duration: number): Documentarie[] {
        return this.collection.filter((documentales) => documentales.duracion === duration);
    }

    /**
     * Método que busca documentales por tópico.
     * @param topic Tópico del documental a buscar.
     * @returns Array de documentales cuyo tópico coincide con el parámetro de entrada.
     */
    searchTopic(topic: string): Documentarie[] {
        return this.collection.filter((documentales) => documentales.topico === topic);
    }
}

/**
 * Clase que representa una colección de películas que se pueden transmitir.
 * Extiende la clase BasicStreamableCollection y utiliza la interfaz Movie.
 */
import { BasicStreamableCollection } from "./BasicStreamableCollection";
import { Movie } from "./Interfaces";

export class PeliculasCollection extends BasicStreamableCollection<Movie> {
    /**
     * Método que devuelve las películas que coinciden con el nombre especificado.
     * @param name El nombre de la película que se busca.
     * @returns Un arreglo de películas que coinciden con el nombre especificado.
     */
    searchName = (name: string) => this.collection.filter((movie) => movie.nombre === name);

    /**
     * Método que devuelve las películas que coinciden con el año especificado.
     * @param year El año de la película que se busca.
     * @returns Un arreglo de películas que coinciden con el año especificado.
     */
    searchYear = (year: number) => this.collection.filter((movie) => movie.año === year);

    /**
     * Método que devuelve las películas que coinciden con la duración especificada.
     * @param duration La duración de la película que se busca.
     * @returns Un arreglo de películas que coinciden con la duración especificada.
     */
    searchDuration = (duration: number) => this.collection.filter((movie) => movie.duracion === duration);
}

/**
 * Una clase genérica que modela una lista de elementos de cualquier tipo.
 */
export class Lista<T> {
  private elementos: T[];

  /**
   * Crea una nueva lista con los elementos especificados.
   * @param elementos Los elementos para incluir en la lista.
   */
  constructor(...elementos: T[]) {
    this.elementos = elementos;
  }

  /**
   * Añade los elementos de una lista al final de esta lista.
   * @param lista La lista de elementos a añadir.
   */
  append(lista: Lista<T>): void {
    this.elementos.push(...lista.toArray());
  }

  /**
   * Combina varias listas en una sola lista que contiene todos sus elementos.
   * @param listas Las listas para combinar.
   * @returns La lista resultante de la combinación.
   */
  concatenate(...listas: Lista<T>[]): Lista<T> {
    const nuevaLista = new Lista<T>(...this.elementos);
    for (const lista of listas) {
      nuevaLista.append(lista);
    }
    return nuevaLista;
  }

  /**
   * Filtra los elementos de la lista según un predicado lógico.
   * @param predicado La función que determina si un elemento debe ser incluido en la lista filtrada.
   * @returns La lista resultante de la filtración.
   */
  filter(predicado: (elemento: T) => boolean): Lista<T> {
    const nuevaLista = new Lista<T>();
    for (const elemento of this.elementos) {
      if (predicado(elemento)) {
        nuevaLista.append(new Lista(elemento));
      }
    }
    return nuevaLista;
  }

  /**
   * Devuelve el número de elementos en la lista.
   * @returns El número de elementos en la lista.
   */
  length(): number {
    return this.elementos.length;
  }

  /**
   * Aplica una función a cada elemento de la lista y devuelve la lista resultante.
   * @param transformador La función que transforma los elementos de la lista.
   * @returns La lista resultante de la transformación.
   */
  map<U>(transformador: (elemento: T) => U): Lista<U> {
    const nuevaLista = new Lista<U>();
    for (const elemento of this.elementos) {
      nuevaLista.append(new Lista(transformador(elemento)));
    }
    return nuevaLista;
  }

  /**
   * Reduce los elementos de la lista a un valor acumulado utilizando una función acumuladora.
   * @param fn La función que acumula los elementos de la lista.
   * @param acumulador El valor inicial del acumulador.
   * @returns El valor resultante de la reducción.
   */
  reduce<U>(fn: (acumulador: U, elemento: T) => U, acumulador: U): U {
    let resultado: U = acumulador;
    for (const elemento of this.elementos) {
      resultado = fn(resultado, elemento);
    }
    return resultado;
  }

  /**
   * Devuelve una lista con los elementos de esta lista en orden inverso.
   * @returns La lista con los elementos en orden inverso.
   */
  reverse(): Lista<T> {
    const nuevaLista = new Lista<T>();
    for (let i = this.elementos.length - 1; i >= 0; i--) {
      nuevaLista.append(new Lista(this.elementos[i]));
    }
    return nuevaLista;
  }
  /**
   * Itera sobre los elementos de la lista y aplica una función a cada uno.
   * @param funcion La función que se aplicará a cada elemento.
   */
  forEach(funcion: (elemento: T) => void): void {
    for (const elemento of this.elementos) {
      funcion(elemento);
    }
  }
  /**
   * Devuelve un array con los elementos de la lista.
   * @returns Un array con los elementos de la lista.
   */
  toArray(): T[] {
    return this.elementos;
  }
}

import { Discografia } from "./Discografia";

export class Artista {
  private _nombre: string;
  private _oyentesMensuales: number;
  private _discografia: Discografia;

  constructor(nombre: string, oyentesMensuales: number, discografia: Discografia) {
    this._nombre = nombre;
    this._oyentesMensuales = oyentesMensuales;
    this._discografia = discografia;
  }

  get nombre(): string {
    return this._nombre;
  }

  get oyentesMensuales(): number {
    return this._oyentesMensuales;
  }

  get discografia(): Discografia {
    return this._discografia;
  }
}

import { Disco, Single } from "./Disco";

export class Discografia {
  private _discos: Array<Disco | Single>;

  constructor(discos: Array<Disco | Single>) {
    this._discos = discos;
  }

  get discos(): Array<Disco | Single> {
    return this._discos;
  }
}
